import { buildNuxt } from '@nuxt/kit';
import { r as resolve, a as relative } from '../shared/nuxi.22378a76.mjs';
import { c as consola } from '../shared/nuxi.9bc4d980.mjs';
import { c as clearDir } from '../shared/nuxi.da7ea8d0.mjs';
import { l as loadKit } from '../shared/nuxi.71bafe5d.mjs';
import { w as writeTypes } from '../shared/nuxi.8cc48d8e.mjs';
import { d as defineNuxtCommand } from '../shared/nuxi.b617cd0e.mjs';
import '../shared/nuxi.ccadf1fb.mjs';
import 'util';
import 'path';
import 'fs';
import 'os';
import 'tty';
import 'node:fs';
import '../shared/nuxi.760576cb.mjs';
import 'node:module';
import 'node:url';
import '../shared/nuxi.e150c956.mjs';

const prepare = defineNuxtCommand({
  meta: {
    name: "prepare",
    usage: "npx nuxi prepare",
    description: "Prepare nuxt for development/build"
  },
  async invoke(args) {
    process.env.NODE_ENV = process.env.NODE_ENV || "production";
    const rootDir = resolve(args._[0] || ".");
    const { loadNuxt } = await loadKit(rootDir);
    const nuxt = await loadNuxt({ rootDir, config: { _prepare: true } });
    await clearDir(nuxt.options.buildDir);
    await buildNuxt(nuxt);
    await writeTypes(nuxt);
    consola.success("Types generated in", relative(process.cwd(), nuxt.options.buildDir));
  }
});

export { prepare as default };
